import React from 'react';
import { Grid, Card, Carousel, Dropdown, Form, Media, Button } from 'react-bootstrap';
import img1 from '../../assets/images/dustbin.jpg'
import './Products.css';

class Orders extends React.Component {

    constructor(props){
        super(props);

        this.state={
            products:props.location.state.p
        }
        const products = props.location.state.p
        console.log(products);
    }

    render() {
        return (
            <div>
            <h1 className="head">Review Order</h1>

            <div className="container">
                <div className="row mt-5">
                    <div className="col-md-3 cc">
                        <Card.Img className="img" variant="top" src={process.env.PUBLIC_URL + this.state.products.image1} rounded/>
                    </div>
                    <div className="col-md-9 cc">
                        <Card className="c">
                            <Card.Body className="text">
                                <Card.Title>{this.state.products.title}</Card.Title>
                                <Card.Text>
                                {this.state.products.desc1}<br/>
                                <h3>${this.state.products.price}</h3><br/>
                                </Card.Text>
                                <Button className="checkoutBtn" variant="primary">Proceed to checkout</Button>
                            </Card.Body>
                        </Card>
                    </div>
                </div>
            </div>

           </div>
        );
    }
}

export default Orders;
